package io.pimwi.infra.util;

import com.google.common.collect.Lists;
import io.pimwi.domain.entities.User;
import io.pimwi.domain.factories.NewsFactory;
import io.pimwi.domain.factories.UserFactory;
import io.pimwi.infra.repository.PersonRepository;
import io.pimwi.infra.repository.SessionRepository;
import io.pimwi.infra.repository.UserRepository;
import org.joda.time.DateTime;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import javax.inject.Inject;
import javax.inject.Named;
import javax.transaction.Transactional;
import java.util.List;

/**
 * Class used to generate consistent data for live testing.
 */
@Named
public class DataGenerator {

    private static Logger logger = LoggerFactory.getLogger(DataGenerator.class);

    private final static int MAX_USERS = 10;

    @Inject
    UserRepository userRepository;

    @Inject
    PersonRepository personRepository;

    @Inject
    SessionRepository sessionRepository;

    /**
     * Generate and persist some users and simulated sessions.
     */
    @Transactional
    public void populateData() {
        logger.info("Generating users ...");

        generateBasicUsers();
        generateGenericUsers();
        generateLinkedUsers();
        generateUserWithNewsAndLinks();
        generateAnotherUserWithNewsAndLinks();
        generateUserWithMessages();

        long count = userRepository.count();
        logger.info("There is " + count + " users in the DB");
    }

    /**
     *
     */
    private void generateBasicUsers() {
        List<User> users = Lists.newArrayList();
        users.add(UserFactory.create("admin", "admin"));
        users.add(UserFactory.create("test", "test"));
        users.add(UserFactory.create("vincent", "1", "Vincent", "Taront", "vincent@mail.com"));
        users.add(UserFactory.create("sylvain", "1", "Sylvain", "Bellone", "sylvain@mail.com"));
        users.add(UserFactory.create("sophie", "1", "Sophie", "Lamour", "sophie@mail.com"));
        users.add(UserFactory.create("melanie", "1", "Mélanie", "Legrand", "melanie@mail.com"));
        users.add(UserFactory.create("geraldine", "1", "Sophie", "Muto", "geraldine@mail.com"));
        users.add(UserFactory.create("joel", "1", "Coach", "Joël", "joel@mail.com"));
        users.add(UserFactory.create("christina", "1", "Christina", "Chazame", "christina@mail.com"));
        users.add(UserFactory.create("isabelle", "1", "Isabelle", "Boulet", "isabelle@mail.com"));
        users.add(UserFactory.create("jean", "1", "Jean", "Cérien", "jean@mail.com"));
        userRepository.save(users);
    }

    /**
     *
     */
    private void generateGenericUsers() {
        List<User> users = Lists.newArrayList();
        // Add MAX_USERS (login = "userX", password = "toto")
        for (int i = 0; i < MAX_USERS; i++) {
            users.add(UserFactory.create("user" + i, "toto"));
        }
        userRepository.save(users);
    }

    /**
     *
     */
    private void generateLinkedUsers() {
        // Create & save users
        User jeremy = UserFactory.create("jeremy", "1", "Jérémy", "Buget", "jeremy@mail.com");
        jeremy.getIdentity().setPicture("data:image/png;base64,");
        jeremy.add(NewsFactory.create(jeremy, "Je refais Facebook (en moins d'une semaine).", (new DateTime("2014-04-01")).toDate()));

        List<User> users = Lists.newArrayList();
        users.add(jeremy);
        users.add(UserFactory.create("lauriane", "1", "Lauriane", "Dupont", "lauriane@mail.com"));
        users.add(UserFactory.create("mathys", "1", "Mathys", "Dupont", "mathys@mail.com"));
        userRepository.save(users);

        // Link users
        link("jeremy", "lauriane");
        link("jeremy", "mathys");
    }

    private void link(String login1, String login2) {
        // Retrieve the 2 users by login
        User user1 = userRepository.findByLogin(login1);
        User user2 = userRepository.findByLogin(login2);

        // Link user2 to user1
        user1.add(user2.getIdentity());

        // Link user1 to user2
        user2.add(user1.getIdentity());
    }

    /**
     *
     */
    private void generateUserWithNewsAndLinks() {
        // Create and persist user
        User user = UserFactory.create("pharrell", "1", "Pharrell", "Williams", "pharrell@mail.com");
        user = userRepository.save(user);

        // Add picture
        user.getIdentity().setPicture("data:image/jpeg;base64,");

        // Add some news
        user.add(NewsFactory.create(user, "Hello world !", (new DateTime("2013-12-21")).toDate()));
        user.add(NewsFactory.create(user, "Example of another news.", (new DateTime("2014-01-14")).toDate()));
        user.add(NewsFactory.create(user, "There is a better way !", (new DateTime("2014-03-09")).toDate()));

        // Link
        link("pharrell", "jeremy");
    }

    private void generateAnotherUserWithNewsAndLinks() {
        // Create and persist user
        User user = UserFactory.create("penelope", "1", "Penelope", "Cruz", "penelope@mail.com");
        user = userRepository.save(user);

        // Add picture
        user.getIdentity().setPicture("data:image/jpeg;base64,");

        // Add some news
        user.add(NewsFactory.create(user, "Buongiorno il mondo !", (new DateTime("2014-01-05")).toDate()));
        user.add(NewsFactory.create(user, "Ce sont les meilleures équipes ; Sie sind die alterbesten Manshaftën ; The main event ; Les grandes équipes ; The champions !!!", (new DateTime("2014-04-03")).toDate()));

        // Link
        link("penelope", "jeremy");
    }

    private void generateUserWithMessages() {
        // Create and persist user
        User user = UserFactory.create("scarlett", "1", "Scarlett", "Johansson", "scarlett@mail.com");
        user = userRepository.save(user);

        // Add picture
        user.getIdentity().setPicture("data:image/jpeg;base64,");

        // Add some news
        user.add(NewsFactory.create(user, "Scarlett Ingrid Johansson est une actrice et chanteuse américaine née le 22 novembre 1984.", (new DateTime("2014-04-08")).toDate()));

        // Link
        link("scarlett", "jeremy");
    }

}
